CREATE PROCEDURE SP_ActualizarUsuario 
	@id int=0,
	@Nombre nvarchar(50)='',
	@Correo nvarchar(50)='',
	@Direccion nvarchar(50)='',
	@Telefono nvarchar(50)=''
AS
BEGIN
	UPDATE TblUsuarios SET Nombre=@Nombre,Correo=@Correo,Direccion=@Direccion,Telefono=@Telefono
	WHERE id=@id
END
GO

